// LandingPage.js
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import Typed from 'typed.js';
import ParticleEffect from '../Particleeffect';
import './Landingstyle.css';

const LandingPage = () => {
  useEffect(() => {
    // Typed.js typing effect
    const introText = "Step into coding's fast lane with our real-time editor—your ideas, instantly tangible. No more waiting; just code, collaborate, and create with unprecedented speed.";

    const typed = new Typed('.lead', {
      strings: [introText],
      typeSpeed: 40, // Adjust typing speed as needed
      showCursor: false, // Set to true if you want a blinking cursor
    });

    // Smooth scroll functionality
    const pageScrollLinks = document.querySelectorAll('.page-scroll');

    pageScrollLinks.forEach(function (link) {
      link.addEventListener('click', function (e) {
        e.preventDefault();

        const targetId = this.getAttribute('href').substring(1);
        const targetElement = document.getElementById(targetId);

        window.scrollTo({
          top: targetElement.offsetTop - 50,
          behavior: 'smooth',
        });
      });
    });

    // Clean up Typed instance on component unmount
    return () => {
      typed.destroy();
    };
  }, []); // Empty dependency array ensures that this effect runs only once after initial render

  return (
    <div>
      {/* ... Your existing JSX code ... */}
      <section className="py-7 py-md-0 bg-hero" id="home">
        <div className="container">
          <div className="row vh-md-100">
            <div className="col-md-8 col-sm-10 col-12 mx-auto my-auto text-center">
              {/* ... Your existing JSX code ... */}
              <p className="lead py-3">Step into coding's fast lane with our real-time editor—your ideas, instantly tangible. No more waiting; just code, collaborate, and create with unprecedented speed.</p>
            </div>
          </div>
        </div>
      </section>

      <ParticleEffect />

      {/* ... Your existing JSX code ... */}
    </div>
  );
};

export default LandingPage;
