// LandingPage.js
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import Typed from 'typed.js';
import ParticleEffect from '../Particleeffect';
import './Landingstyle.css';

const LandingPage = () => {
  useEffect(() => {
    // Typed.js typing effect
    const introText = "Step into coding's fast lane with our real-time editor—your ideas, instantly tangible. No more waiting; just code, collaborate, and create with unprecedented speed.";

    const typed = new Typed('.lead', {
      strings: [introText],
      typeSpeed: 40, // Adjust typing speed as needed
      showCursor: false, // Set to true if you want a blinking cursor
    });

    // Clean up Typed instance on component unmount
    return () => {
      typed.destroy();
    };
  }, []); // Empty dependency array ensures that this effect runs only once after initial render

  return (
    <div>
      <section className="py-7 py-md-0 bg-hero" id="home">
        <div className="container">
          <div className="row vh-md-100">
            <div className="col-md-8 col-sm-10 col-12 mx-auto my-auto text-center">
              <h1 className="heading-black text-capitalize fadeInLeft">Code-pulse</h1>
              <h2 className="sub-heading text-capitalize fadeInLeft">The real-time code editor that unleashes your creativity.</h2>
              {/* Add class 'lead' to the paragraph for Typed.js to target */}
              <p className="lead py-3 fadeInLeft">Step into coding's fast lane with our real-time editor—your ideas, instantly tangible. No more waiting; just code, collaborate, and create with unprecedented speed.</p>

              {/* Link to navigate to Home.js */}
              <Link to="/home">
                <button className="btn btn-primary d-inline-flex flex-row align-items-center">
                  Get started now
                  <em className="ml-2" data-feather="arrow-right"></em>
                </button>
              </Link>
            </div>
          </div>
        </div>
      </section>

      <ParticleEffect />

      {/* Footer Section */}
      <div className="footer">
        &copy; 2023 Codepulse - All Rights Reserved
      </div>
    </div>
  );
};

export default LandingPage;
